mapper.select:
select count(*) as y, date_format(create_time, '%Y-%m-%d') as x from user 
where DATE_SUB(CURDATE(), INTERVAL 7 DAY) <= date(create_time)
group by date_format(create_time, '%Y-%m-%d');

select count(*) as y, CONCAT(CAST(date_format(create_time, '%m') AS SIGNED),'æœˆ') as x from user 
where date_format(create_time, '%Y') ='2019' 
group by date_format(create_time, '%m');
